import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [scrollY, setScrollY] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Calcular o progresso do scroll (0 a 1)
  const maxScroll = window.innerHeight * 2; // Altura de 2 viewports para o efeito completo
  const scrollProgress = Math.min(scrollY / maxScroll, 1);

  // Calcular o zoom da palavra "lovers" (começa grande e diminui)
  const initialScale = 8; // Escala inicial bem grande
  const finalScale = 1; // Escala final normal
  const loversScale = initialScale - (initialScale - finalScale) * scrollProgress;

  // Calcular a opacidade da imagem do casal (fade out no final)
  const coupleOpacity = scrollProgress < 0.8 ? 1 : 1 - ((scrollProgress - 0.8) / 0.2);

  // Posição da imagem do casal dentro da palavra lovers
  const couplePosition = {
    x: scrollProgress * 20, // Ajuste fino da posição horizontal
    y: scrollProgress * 10   // Ajuste fino da posição vertical
  };

  return (
    <div className="App">
      {/* Seção principal com altura estendida para permitir scroll */}
      <div className="main-section">

        {/* Imagem do casal - fixada na tela */}
        <div 
          className="couple-image-container"
          style={{
            opacity: coupleOpacity,
            transform: `translate(${couplePosition.x}px, ${couplePosition.y}px)`
          }}
        >
          <img 
            src="/couple-beach.jpg" 
            alt="Casal na praia" 
            className="couple-image"
          />
        </div>

        {/* Palavra "lovers" com zoom */}
        <div 
          className="lovers-container"
          style={{
            transform: `scale(${loversScale})`,
            opacity: scrollProgress > 0.1 ? 1 : 0 // Aparece gradualmente
          }}
        >
          <img 
            src="/lovers-text.png" 
            alt="Lovers" 
            className="lovers-text"
          />
        </div>

        {/* Indicador de scroll */}
        <div className="scroll-indicator">
          <p>Role para baixo para ver a mágica acontecer ❤️</p>
          <div className="arrow-down">⬇️</div>
        </div>

      </div>

      {/* Seção final */}
      <div className="final-section">
        <div className="final-content">
          <h1>❤️ Feliz Dia dos Namorados! ❤️</h1>
          <p>Que nosso amor continue crescendo a cada dia...</p>
        </div>
      </div>
    </div>
  );
}

export default App;
