import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [scrollY, setScrollY] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Calcular o progresso do scroll (0 a 1)
  const maxScroll = window.innerHeight * 4; // Altura de 4 viewports para o efeito completo
  const scrollProgress = Math.min(scrollY / maxScroll, 1);

  // Dividir o efeito em fases
  const phase1End = 0.25; // Primeira fase: imagem tela cheia
  const phase2End = 0.5;  // Segunda fase: palavra aparece tela cheia
  const phase3End = 0.75; // Terceira fase: ambas diminuem
  const phase4End = 1.0;  // Quarta fase: máscara aplicada

  // Opacidade da imagem completa
  const completeImageOpacity = scrollProgress < phase3End ? 1 : 1 - ((scrollProgress - phase3End) / (phase4End - phase3End));

  // Opacidade da palavra lovers (aparece na fase 2)
  const loversOpacity = scrollProgress > phase1End ? 1 : 0;

  // Escala da imagem completa (tela cheia -> normal)
  const imageScale = scrollProgress < phase2End ? 
    3 - (scrollProgress / phase2End) * 1.5 : // De 3x para 1.5x
    1.5 - ((scrollProgress - phase2End) / (phase3End - phase2End)) * 0.5; // De 1.5x para 1x

  // Escala da palavra lovers (tela cheia -> normal)
  const loversScale = scrollProgress < phase2End ? 
    5 : // Começa gigante (5x)
    5 - ((scrollProgress - phase2End) / (phase3End - phase2End)) * 4; // Diminui para 1x

  // Opacidade da imagem mascarada (aparece na fase 4)
  const maskedImageOpacity = scrollProgress > phase3End ? (scrollProgress - phase3End) / (phase4End - phase3End) : 0;

  return (
    <div className="App">
      {/* Seção principal com altura estendida para permitir scroll */}
      <div className="main-section">

        {/* Imagem do casal completa - tela cheia inicialmente */}
        <div 
          className="couple-complete-container"
          style={{
            opacity: completeImageOpacity,
            transform: `translate(-50%, -50%) scale(${imageScale})`,
          }}
        >
          <img 
            src="/couple-beach.jpg" 
            alt="Casal na praia" 
            className="couple-complete-image"
          />
        </div>

        {/* Container principal com a palavra "lovers" e a imagem mascarada */}
        <div 
          className="lovers-container"
          style={{
            transform: `translate(-50%, -50%) scale(${loversScale})`,
            opacity: loversOpacity
          }}
        >
          {/* Imagem do casal mascarada - visível na fase 4 */}
          <div 
            className="couple-masked"
            style={{
              opacity: maskedImageOpacity,
            }}
          >
            <img 
              src="/couple-beach.jpg" 
              alt="Casal na praia" 
              className="couple-image-masked"
            />
          </div>

          {/* Palavra "lovers" */}
          <img 
            src="/lovers-text.png" 
            alt="Lovers" 
            className="lovers-text-outline"
          />
        </div>

        {/* Indicador de scroll */}
        <div className="scroll-indicator">
          <p>Role para baixo para ver a mágica acontecer ❤️</p>
          <div className="arrow-down">⬇️</div>
        </div>

      </div>

      {/* Seção final */}
      <div className="final-section">
        <div className="final-content">
          <h1>❤️ Feliz Dia dos Namorados! ❤️</h1>
          <p>Que nosso amor continue crescendo a cada dia...</p>
        </div>
      </div>
    </div>
  );
}

export default App;
