import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [scrollY, setScrollY] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Calcular o progresso do scroll (0 a 1)
  const maxScroll = window.innerHeight * 3; // Altura de 3 viewports para o efeito completo
  const scrollProgress = Math.min(scrollY / maxScroll, 1);

  // Dividir o efeito em 3 etapas
  const phase1End = 0.33; // Primeira fase: imagem completa
  const phase2End = 0.66; // Segunda fase: palavra aparece
  const phase3End = 1.0;  // Terceira fase: máscara e zoom

  // Fase 1: Imagem completa (0 a 0.33)
  const isPhase1 = scrollProgress <= phase1End;

  // Fase 2: Palavra aparece (0.33 a 0.66)
  const isPhase2 = scrollProgress > phase1End && scrollProgress <= phase2End;
  const phase2Progress = isPhase2 ? (scrollProgress - phase1End) / (phase2End - phase1End) : 0;

  // Fase 3: Máscara e zoom (0.66 a 1.0)
  const isPhase3 = scrollProgress > phase2End;
  const phase3Progress = isPhase3 ? (scrollProgress - phase2End) / (phase3End - phase2End) : 0;

  // Opacidade da imagem completa (desaparece na fase 3)
  const completeImageOpacity = isPhase3 ? 1 - phase3Progress * 0.5 : 1;

  // Opacidade da palavra lovers (aparece na fase 2)
  const loversOpacity = scrollProgress > phase1End ? Math.min((scrollProgress - phase1End) / 0.2, 1) : 0;

  // Escala da palavra lovers (zoom out na fase 3)
  const initialScale = 3;
  const finalScale = 1;
  const loversScale = isPhase3 ? initialScale - (initialScale - finalScale) * phase3Progress : initialScale;

  // Opacidade da imagem mascarada (aparece na fase 3)
  const maskedImageOpacity = isPhase3 ? phase3Progress : 0;

  return (
    <div className="App">
      {/* Seção principal com altura estendida para permitir scroll */}
      <div className="main-section">

        {/* Imagem do casal completa - visível nas fases 1 e 2 */}
        <div 
          className="couple-complete-container"
          style={{
            opacity: completeImageOpacity,
          }}
        >
          <img 
            src="/couple-beach.jpg" 
            alt="Casal na praia" 
            className="couple-complete-image"
          />
        </div>

        {/* Container principal com a palavra "lovers" e a imagem mascarada */}
        <div 
          className="lovers-container"
          style={{
            transform: `scale(${loversScale})`,
            opacity: loversOpacity
          }}
        >
          {/* Imagem do casal mascarada - visível na fase 3 */}
          <div 
            className="couple-masked"
            style={{
              opacity: maskedImageOpacity,
            }}
          >
            <img 
              src="/couple-beach.jpg" 
              alt="Casal na praia" 
              className="couple-image-masked"
            />
          </div>

          {/* Palavra "lovers" */}
          <img 
            src="/lovers-text.png" 
            alt="Lovers" 
            className="lovers-text-outline"
          />
        </div>

        {/* Indicador de scroll */}
        <div className="scroll-indicator">
          <p>Role para baixo para ver a mágica acontecer ❤️</p>
          <div className="arrow-down">⬇️</div>
        </div>

      </div>

      {/* Seção final */}
      <div className="final-section">
        <div className="final-content">
          <h1>❤️ Feliz Dia dos Namorados! ❤️</h1>
          <p>Que nosso amor continue crescendo a cada dia...</p>
        </div>
      </div>
    </div>
  );
}

export default App;
